mapper.delete:
update student
set student_status = 1
where student_no = #{studentNo,jdbcType=VARCHAR}

mapper.select:
select student_id, student_no, class_no, grade_no, `name`, sex, course, success,student_status
from student
where student_no = #{studentNo,jdbcType=VARCHAR}

select student_id, student_no, class_no, grade_no, `name`, sex, course, success,student_status
from student left join course
on student.student_no = course.student_no


    AND class_no = #{student.classNo,jdbcType=VARCHAR}


    AND student_no = #{student.studentNo,jdbcType=VARCHAR}


    AND sex = #{student.sex,jdbcType=VARCHAR}


    AND course = #{student.course,jdbcType=VARCHAR}



          success >= 60


          success < 60
